import React, { useState } from 'react';
import { questions } from './questions.js'
import { Info_Box } from './otherfunctions.js'

export default function App() {

    // start button on click
    const [StartButton, setStartButton] = useState(true);
    const [InfoBox, setInfoBox] = useState(false);

    const handleClick = event => {
    setStartButton(false);
    setInfoBox(true);
      };


	const numberquestions = 2;
	const array = Array(numberquestions)
   		.fill()
    	.map(() => Math.floor((questions.length) * Math.random()));
	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);


	const handleAnswerOptionClick = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		}

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < numberquestions) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
	return (
	<div>
    	<div>
    	{StartButton && (
    	<button id="start_btn" class="start_btn" onClick={()=>handleClick()}>Start Quiz</button>)}
    	</div>

       <div>
       {InfoBox && <Info_Box />}
       </div>




    </div>

	);
}
