import React, { Component } from 'react';

// Box with Quiz basic info
export function Info_Box() {
  return (
  <div class="boxinfo">
      <div class="info-title"><span>Some Rules of this Quiz</span></div>
      <div class="info-list">
       <div class="info">1. Once you select your answer, it cannot be undone.</div>
       <div class="info">2. You cannot exit from the Quiz while you are playing.</div>
       <div class="info">3. You will get points on the basis of your correct answers.</div>
      </div>
      <div  class="buttons">
          <button class="quit">Exit Quiz</button>
          <button class="restart">Continue</button>
      </div>
  </div>
  
  );
}
