import React, { useState } from 'react';

export default function App() {
	const questions = [
		{
			questionText: 'Uma mulher de 56 anos vem à consulta por dor abdominal persistente no quadrante inferior esquerdo com três dias de evolução. A dor associa-se a fezes mais moles e estado febril de baixa intensidade. Refere ter comido um gelado numa esplanada há três dias. Ela não tem antecedentes patológicos de relevo e não faz medicação habitual. Os resultados da citologia e mamografia realizadas há duas semanas encontravam-se dentro dos parâmetros de normalidade. A colonoscopia realizada há três anos (má preparação) não revelou alterações patológicas. Os seus sinais vitais hoje são temperatura 37,7°C, frequência respiratória 18/min, pulso rítmico com frequência cardíaca 88/min e pressão arterial 138/78 mm Hg. O exame abdominal permite verificar sensibilidade dolorosa aumentada, defesa no quadrante inferior esquerdo baixo e redução dos ruídos hidroaéreos. O exame retal revela fezes soltas e moles, nas quais uma pesquisa de sangue oculto é positiva. Qual dos seguintes é o diagnóstico inicial mais provável?',
			answerOptions: [
				{ answerText: 'Cancro do cólon.', isCorrect: false },
				{ answerText: 'Colite ulcerosa.', isCorrect: false },
				{ answerText: 'Diverticulite.', isCorrect: true },
				{ answerText: 'Intolerância à lactose.', isCorrect: false },
				{ answerText: 'Síndrome do cólon irritável.', isCorrect: false },
			],
		},
		{
			questionText: 'Who is CEO of Tesla?',
			answerOptions: [
				{ answerText: 'Jeff Bezos', isCorrect: false },
				{ answerText: 'Elon Musk', isCorrect: true },
				{ answerText: 'Bill Gates', isCorrect: false },
				{ answerText: 'Tony Stark', isCorrect: false },
			],
		},
		{
			questionText: 'The iPhone was created by which company?',
			answerOptions: [
				{ answerText: 'Apple', isCorrect: true },
				{ answerText: 'Intel', isCorrect: false },
				{ answerText: 'Amazon', isCorrect: false },
				{ answerText: 'Microsoft', isCorrect: false },
			],
		},
		{
			questionText: 'How many Harry Potter books are there?',
			answerOptions: [
				{ answerText: '1', isCorrect: false },
				{ answerText: '4', isCorrect: false },
				{ answerText: '6', isCorrect: false },
				{ answerText: '7', isCorrect: true },
			],
		},
		{
			questionText: '1,2,3,4?',
			answerOptions: [
				{ answerText: '1', isCorrect: false },
				{ answerText: '2', isCorrect: false },
				{ answerText: '3', isCorrect: false },
				{ answerText: '4', isCorrect: true },
			],
		},
		{
			questionText: '5,6,7,8?',
			answerOptions: [
				{ answerText: '5', isCorrect: false },
				{ answerText: '6', isCorrect: false },
				{ answerText: '7', isCorrect: false },
				{ answerText: '8', isCorrect: true },
			],
		},
	];

	const numberquestions = 2;
	const array = Array(numberquestions)
   		.fill()
    	.map(() => Math.floor((questions.length) * Math.random()));
	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);
	const resetStateClick =()=>{
        setCurrentQuestion (0)
        setShowScore(false)
        setScore(0)
    }


	const handleAnswerOptionClick = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		}

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < numberquestions) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
	return (
		<div className='app'>
			{showScore ? (
				<div className='score-section'>
					You scored {score} out of {numberquestions}
					<button onClick={resetStateClick}>Retry</button>
				</div>
			) : (
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Question {currentQuestion + 1}</span>/{numberquestions}
						</div>
						<div className='question-text'>{questions[array[currentQuestion]].questionText}</div>
					</div>
					<div className='answer-section'>
						{questions[array[currentQuestion]].answerOptions.map((answerOption) => (
							<button onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}>{answerOption.answerText}</button>
						))}
					</div>
				</>
			)}
		</div>
	);
}
